COPY (WITH constant AS (VALUES (TIMESTAMP %s)) SELECT crg.research_group_name, crg.research_group_contact_name, crg.research_group_contact_phone, crg.sourcing, st.st FROM casmart_research_group AS crg INNER JOIN ship_to_casmart_research_group AS stcrg ON stcrg.research_group_id = crg.research_group_id INNER JOIN ship_to AS st ON stcrg.st = st.st WHERE crg.updated_date >= (table constant) OR st.updated_date >= (table constant)) TO STDOUT WITH CSV DELIMITER ',' HEADER